                                <>
                                  <div className="pie-chart">
                                    {segments.map((segment, index) => (
                                      <div 
                                        key={index}
                                        className="pie-segment"
                                        style={{
                                          '--percentage': `${segment.percentage}%`,
                                          '--color': segment.color,
                                          '--rotation': `${segments.slice(0, index).reduce((sum, s) => sum + parseFloat(s.percentage), 0) * 3.6}deg`
                                        }}
                                      />
                                    ))}
                                  </div>
                                  <div className="pie-legend">
                                    {segments.map((segment, index) => (
                                      <div key={index} className="legend-item">
                                        <div className="legend-color" style={{backgroundColor: segment.color}}></div>
                                        <div className="legend-label">
                                          <strong>{segment.label}</strong>
                                          <span>{segment.value} ({segment.percentage}%)</span>
                                        </div>
                                      </div>
                                    ))}
                                  </div>
                                </>
                              );
                            })()}
                          </div>
                        </div>
                      </div>

                      {/* Top Itineraries by Activity */}
                      {itineraries.length > 0 && (
                        <div className="chart-container" style={{marginTop: '2rem'}}>
                          <h3 className="chart-title">ðŸ† Most Active Itineraries</h3>
                          <div className="top-users-grid">
                            {itineraries
                              .map(itin => ({
                                ...itin,
                                destCount: destinations[itin.id]?.length || 0,
                                actCount: activities[destinations[itin.id]?.[0]?.destinationId]?.content?.length || 0,
                                bookCount: bookings[itin.id]?.content?.length || 0,
                                expCount: expenses[itin.id]?.length || 0,
                                totalActivity: (destinations[itin.id]?.length || 0) + 
                                              (bookings[itin.id]?.content?.length || 0) + 
                                              (expenses[itin.id]?.length || 0)
                              }))
                              .sort((a, b) => b.totalActivity - a.totalActivity)
                              .slice(0, 5)
                              .map((itin, index) => (
                                <div key={itin.id} className="top-user-card">
                                  <div className="user-rank">{index + 1}</div>
                                  <div className="user-avatar">
                                    <svg width="40" height="40" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                      <polygon points="1 6 1 22 8 18 16 22 23 18 23 2 16 6 8 2 1 6"/>
                                      <line x1="8" y1="2" x2="8" y2="18"/>
                                      <line x1="16" y1="6" x2="16" y2="22"/>
                                    </svg>
                                  </div>
                                  <div className="user-stats">
                                    <h4>{itin.tripName}</h4>
                                    <p className="total-activity">{itin.totalActivity} Total Items</p>
                                    <div className="mini-stats">
                                      <span title="Destinations">ðŸ“ {itin.destCount}</span>
                                      <span title="Bookings">ðŸŽ« {itin.bookCount}</span>
                                      <span title="Expenses">ðŸ’° {itin.expCount}</span>
                                    </div>
                                  </div>
                                </div>
                              ))}
                          </div>
                        </div>
                      )}

                      {/* Insights Cards */}
                      <div className="insights-grid">
                        <div className="insight-card">
                          <h4>ðŸ“ˆ Avg Destinations per Trip</h4>
                          <p className="insight-value">
                            {totalItineraries > 0 
                              ? (totalDestinations / totalItineraries).toFixed(1)
                              : 0}
                          </p>
                        </div>
                        <div className="insight-card">
                          <h4>ðŸŽ¯ Avg Activities per Trip</h4>
                          <p className="insight-value">
                            {totalItineraries > 0 
                              ? (totalActivities / totalItineraries).toFixed(1)
                              : 0}
                          </p>
                        </div>
                        <div className="insight-card">
                          <h4>ðŸ’° Total Expenses</h4>
                          <p className="insight-value">{totalExpenses}</p>
                        </div>
                        <div className="insight-card">
                          <h4>âœˆï¸ Completion Rate</h4>
                          <p className="insight-value">
                            {totalItineraries > 0 
                              ? ((statusCounts['COMPLETED'] || 0) / totalItineraries * 100).toFixed(0)
                              : 0}%
                          </p>
                        </div>
                      </div>

                      {itineraries.length === 0 && (
                        <div className="empty-state" style={{marginTop: '3rem'}}>
                          <div className="empty-icon">ðŸ“Š</div>
                          <h3>No Data Yet</h3>
                          <p>Start creating itineraries to see your personalized analytics!</p>
                          <button 
                            className="btn-primary" 
                            onClick={() => setActiveSection('itineraries')}
                            style={{marginTop: '1rem'}}
                          >
                            ðŸ—ºï¸ Create Your First Itinerary
                          </button>
                        </div>
                      )}
                    </>
                  );
                })()}
              </div>
            )}

            {/* PROFILE SECTION */}
            {activeSection === 'profile' && (
              <div className="content-section">
                <div className="section-header">
                  <h2>ðŸ‘¤ My Profile</h2>
                  {!isEditingProfile ? (
                    <button className="btn-primary" onClick={() => setIsEditingProfile(true)}>
                      âœï¸ Edit Profile
                    </button>
                  ) : (
                    <button className="btn-secondary" onClick={() => {
                      setIsEditingProfile(false);
                      fetchUserProfile();
                    }}>
                      âœ• Cancel
                    </button>
                  )}
                </div>

                {userProfile && !isEditingProfile && (
                  <div className="profile-view">
                    <div className="profile-card">
                      <div className="profile-header">
                        <div className="profile-avatar-large">
                          {userProfile.username?.charAt(0).toUpperCase() || 'U'}
                        </div>
                        <div className="profile-info">
                          <h3>{userProfile.username}</h3>
                          <span className={`status-badge status-${userProfile.status?.toLowerCase() || 'active'}`}>
                            {userProfile.status || 'ACTIVE'}
                          </span>
                        </div>
                      </div>
                      
                      <div className="profile-details">
                        <div className="detail-row">
                          <span className="detail-label">ðŸ“§ Email:</span>
                          <span className="detail-value">{userProfile.email}</span>
                        </div>
                        <div className="detail-row">
                          <span className="detail-label">ðŸ“± Phone:</span>
                          <span className="detail-value">{userProfile.phone || 'Not provided'}</span>
                        </div>
                        <div className="detail-row">
                          <span className="detail-label">ðŸ  Address:</span>
                          <span className="detail-value">{userProfile.address || 'Not provided'}</span>
                        </div>
                        <div className="detail-row">
                          <span className="detail-label">ðŸ™ï¸ City:</span>
                          <span className="detail-value">{userProfile.city || 'Not provided'}</span>
                        </div>
                        <div className="detail-row">
                          <span className="detail-label">ðŸ“ State/Region:</span>
                          <span className="detail-value">{userProfile.state || 'Not provided'}</span>
                        </div>
                        <div className="detail-row">
                          <span className="detail-label">ðŸŒ Country:</span>
                          <span className="detail-value">{userProfile.country || 'Not provided'}</span>
                        </div>
                        <div className="detail-row">
                          <span className="detail-label">ðŸ“… Member Since:</span>
                          <span className="detail-value">
                            {userProfile.createdDate ? new Date(userProfile.createdDate).toLocaleDateString() : 'N/A'}
                          </span>
                        </div>
                        <div className="detail-row">
                          <span className="detail-label">ðŸ” Last Login:</span>
                          <span className="detail-value">
                            {userProfile.lastLogin ? new Date(userProfile.lastLogin).toLocaleDateString() : 'N/A'}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                )}

                {isEditingProfile && (
                  <div className="form-card">
                    <h3>Edit Your Profile</h3>
                    <form onSubmit={handleProfileUpdate}>
                      <div className="form-grid">
                        <div className="form-group">
                          <label>Username</label>
                          <input 
                            name="username" 
                            placeholder="Your username" 
                            value={profileForm.username} 
                            onChange={handleProfileChange} 
                            required 
                          />
                        </div>
                        
                        <div className="form-group">
                          <label>Email</label>
                          <input 
                            name="email" 
                            type="email" 
                            placeholder="Your email" 
                            value={profileForm.email} 
                            onChange={handleProfileChange} 
                            required 
                          />
                        </div>

                        <div className="form-group">
                          <label>Phone</label>
                          <input 
                            name="phone" 
                            type="tel" 
                            placeholder="Your phone number" 
                            value={profileForm.phone} 
                            onChange={handleProfileChange} 
                          />
                        </div>

                        <div className="form-group full-width">
                          <label>Address</label>
                          <input 
                            name="address" 
                            placeholder="Your street address" 
                            value={profileForm.address} 
                            onChange={handleProfileChange} 
                          />
                        </div>

                        <div className="form-group">
                          <label>City</label>
                          <select 
                            name="city" 
                            value={profileForm.city} 
                            onChange={handleProfileChange}
                          >
                            <option value="">Select City</option>
                            <option value="Chennai">Chennai</option>
                            <option value="Bangalore">Bangalore</option>
                            <option value="Hyderabad">Hyderabad</option>
                            <option value="Mumbai">Mumbai</option>
                            <option value="Delhi">Delhi</option>
                            <option value="Kolkata">Kolkata</option>
                            <option value="Pune">Pune</option>
                            <option value="Ahmedabad">Ahmedabad</option>
                            <option value="Jaipur">Jaipur</option>
                            <option value="Lucknow">Lucknow</option>
                            <option value="Kochi">Kochi</option>
                            <option value="Coimbatore">Coimbatore</option>
                            <option value="Visakhapatnam">Visakhapatnam</option>
                            <option value="Indore">Indore</option>
                            <option value="Bhopal">Bhopal</option>
                            <option value="Chandigarh">Chandigarh</option>
                            <option value="Goa">Goa</option>
                            <option value="Other">Other</option>
                          </select>
                        </div>

                        <div className="form-group">
                          <label>State/Region</label>
                          <select 
                            name="state" 
                            value={profileForm.state} 
                            onChange={handleProfileChange}
                          >
                            <option value="">Select State/Region</option>
                            <option value="Tamil Nadu">Tamil Nadu</option>
                            <option value="Karnataka">Karnataka</option>
                            <option value="Kerala">Kerala</option>
                            <option value="Andhra Pradesh">Andhra Pradesh</option>
                            <option value="Telangana">Telangana</option>
                            <option value="Maharashtra">Maharashtra</option>
                            <option value="Gujarat">Gujarat</option>
                            <option value="Rajasthan">Rajasthan</option>
                            <option value="Delhi">Delhi</option>
                            <option value="Punjab">Punjab</option>
                            <option value="Haryana">Haryana</option>
                            <option value="Uttar Pradesh">Uttar Pradesh</option>
                            <option value="West Bengal">West Bengal</option>
                            <option value="Odisha">Odisha</option>
                            <option value="Bihar">Bihar</option>
                            <option value="Madhya Pradesh">Madhya Pradesh</option>
                            <option value="Goa">Goa</option>
                            <option value="Other">Other</option>
                          </select>
                        </div>

                        <div className="form-group">
                          <label>Country</label>
                          <select 
                            name="country" 
                            value={profileForm.country} 
                            onChange={handleProfileChange}
                          >
                            <option value="">Select Country</option>
                            <option value="India">India</option>
                            <option value="United States">United States</option>
                            <option value="United Kingdom">United Kingdom</option>
                            <option value="Canada">Canada</option>
                            <option value="Australia">Australia</option>
                            <option value="Singapore">Singapore</option>
                            <option value="UAE">UAE</option>
                            <option value="Germany">Germany</option>
                            <option value="France">France</option>
                            <option value="Other">Other</option>
                          </select>
                        </div>

                        <div className="form-group">
                          <label>Status</label>
                          <select 
                            name="status" 
                            value={profileForm.status} 
                            onChange={handleProfileChange}
                          >
                            <option value="ACTIVE">Active</option>
                            <option value="INACTIVE">Inactive</option>
                            <option value="PENDING">Pending</option>
                          </select>
                        </div>
                      </div>
                      
                      <div className="form-actions">
                        <button type="submit" className="btn-primary">
                          ðŸ’¾ Save Changes
                        </button>
                        <button 
                          type="button" 
                          className="btn-secondary" 
                          onClick={() => {
                            setIsEditingProfile(false);
                            fetchUserProfile();
                          }}
                        >
                          Cancel
                        </button>
                      </div>
                    </form>
                  </div>
                )}
              </div>
            )}
          </div>
        </main>
      </div>
    </div>
  );
};

export default UserDashboard;
