import React, { createContext, useState, useContext, useEffect } from 'react';

// Create Theme Context
const ThemeContext = createContext(null);

// Custom hook to use Theme Context
export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

// Theme Provider Component
export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState('light');
  const [primaryColor, setPrimaryColor] = useState('#667eea');
  const [secondaryColor, setSecondaryColor] = useState('#764ba2');

  // Initialize theme from localStorage
  useEffect(() => {
    const storedTheme = localStorage.getItem('theme');
    const storedPrimaryColor = localStorage.getItem('primaryColor');
    const storedSecondaryColor = localStorage.getItem('secondaryColor');

    if (storedTheme) {
      setTheme(storedTheme);
      document.documentElement.setAttribute('data-theme', storedTheme);
    }

    if (storedPrimaryColor) {
      setPrimaryColor(storedPrimaryColor);
      document.documentElement.style.setProperty('--primary', storedPrimaryColor);
    }

    if (storedSecondaryColor) {
      setSecondaryColor(storedSecondaryColor);
      document.documentElement.style.setProperty('--secondary', storedSecondaryColor);
    }
  }, []);

  // Toggle between light and dark theme
  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
  };

  // Set specific theme
  const setSpecificTheme = (newTheme) => {
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
  };

  // Update primary color
  const updatePrimaryColor = (color) => {
    setPrimaryColor(color);
    localStorage.setItem('primaryColor', color);
    document.documentElement.style.setProperty('--primary', color);
  };

  // Update secondary color
  const updateSecondaryColor = (color) => {
    setSecondaryColor(color);
    localStorage.setItem('secondaryColor', color);
    document.documentElement.style.setProperty('--secondary', color);
  };

  // Reset to default theme
  const resetTheme = () => {
    setTheme('light');
    setPrimaryColor('#667eea');
    setSecondaryColor('#764ba2');
    localStorage.removeItem('theme');
    localStorage.removeItem('primaryColor');
    localStorage.removeItem('secondaryColor');
    document.documentElement.setAttribute('data-theme', 'light');
    document.documentElement.style.setProperty('--primary', '#667eea');
    document.documentElement.style.setProperty('--secondary', '#764ba2');
  };

  // Check if dark mode is active
  const isDark = theme === 'dark';

  // Check if light mode is active
  const isLight = theme === 'light';

  const value = {
    // State
    theme,
    primaryColor,
    secondaryColor,
    isDark,
    isLight,
    
    // Functions
    toggleTheme,
    setSpecificTheme,
    updatePrimaryColor,
    updateSecondaryColor,
    resetTheme,
  };

  return (
    <ThemeContext.Provider value={value}>
      {children}
    </ThemeContext.Provider>
  );
};

export default ThemeContext;
